mapper.select:
SELECT wc.external_userid, wc.`name`, wc.avatar, wc.type, wc.gender, wc.unionid, wc.birthday, wc.corp_name, wc.corp_full_name, wc.position, wc.is_open_chat, wfcr.id as wfcrId, wfcr.user_id, wfcr.remark, wfcr.description, wfcr.create_time, wfcr.remark_corp_name, wfcr.oper_userid, wfcr.add_way, wfcr.state, wfcr.status, wu.user_name, (SELECT wd.`name` FROM we_department wd WHERE wd.id=wu.main_department and wd.corp_id = #{corpId} ${@com.easyink.common.constant.GenConstants@LIMIT_1}) as department, GROUP_CONCAT(wt.tag_id) AS markTagIds FROM we_customer wc LEFT JOIN we_flower_customer_rel wfcr ON wfcr.corp_id = wc.corp_id and wc.external_userid=wfcr.external_userid LEFT JOIN we_user wu ON wu.corp_id = wc.corp_id and wu.user_id=wfcr.user_id LEFT JOIN we_flower_customer_tag_rel wfcrf ON wfcrf.flower_customer_rel_id = wfcr.id LEFT JOIN we_tag wt ON wt.tag_id = wfcrf.tag_id
wc.corp_id = #{corpId} AND wfcr.`status` = '${@com.easyink.common.constant.Constants@NORMAL_CODE}' ${params.dataScope}
AND wc.gender=#{gender}
AND wfcr.user_id in
#{item}
AND wfcr.user_id=#{userIds}
AND wt.tag_id in
#{item}
AND wt.tag_id=#{tagIds}
AND wfcr.id NOT IN ( SELECT rel1.flower_customer_rel_id FROM we_flower_customer_tag_rel rel1 WHERE rel1.tag_id in
#{item}
)
AND wfcr.id NOT IN ( SELECT rel2.flower_customer_rel_id FROM we_flower_customer_tag_rel rel2 WHERE rel2.tag_id = #{filterTags} )
AND wfcr.status=#{status}
AND wfcr.create_time between #{customerStartTime} and #{customerEndTime}
AND wfcr.add_way = #{addWay}
AND wc.birthday BETWEEN
#{item}
AND wu.is_activate = '${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}'
GROUP BY wfcr.external_userid ORDER BY wfcr.create_time DESC

