AND wc.birthday >=#{startTime} AND wc.birthday <=#{endTime}
AND wc.corp_full_name like concat('%', #{corpFullName}, '%')
AND wfcr.remark_mobiles=#{phone}
AND wfcr.description like concat('%', #{desc}, '%')
