mapper.insert:
insert into we_customer
external_userid, corp_id, name, avatar, type, gender, unionid, birthday, corp_name, corp_full_name, position, is_open_chat
#{externalUserid}, #{corpId}, #{name}, #{avatar}, #{type}, #{gender}, #{unionid}, #{birthday}, #{corpName}, #{corpFullName}, #{position}, #{isOpenChat}

mapper.update:
update we_customer
external_userid = #{externalUserid}, name = #{name}, unionid = #{unionid}, avatar = #{avatar}, corp_id = #{corpId}, type = #{type}, gender = #{gender}, unionid = #{unionid}, birthday = #{birthday}, corp_name = #{corpName}, corp_full_name = #{corpFullName}, position = #{position}, is_open_chat = #{isOpenChat}
where external_userid = #{externalUserid} and corp_id = #{corpId}

UPDATE we_customer
SET birthday = ifnull(#{birthday}, '0000-00-00 00:00:00')
WHERE external_userid = #{externalUserid}
AND corp_id = #{corpId}

mapper.select:
SELECT wc.external_userid, wc.corp_id, wc.`name`, wc.avatar, wc.type, wc.gender, wc.unionid, wc.birthday, wc.corp_name, wc.corp_full_name, wc.position, wc.is_open_chat, wc.type, wfcr.id as wfcrId, wfcr.user_id, wfcr.remark, wfcr.description, wfcr.create_time, wfcr.remark_corp_name, wfcr.oper_userid, wfcr.add_way, wfcr.state, wfcr.description, wfcr.remark_mobiles, wfcr.email, wfcr.address, wfcr.status, wu.user_name, wfcrf.flower_customer_rel_id, wt.tag_id, wt.create_time, wt.`name` as tagName, wcepr.corp_id as wceprCorpId, wcepr.external_userid as wceprExternalUserid, wcepr.extend_property_id, wcepr.property_value, (SELECT wd.`name` FROM we_department wd WHERE wd.id=wu.main_department and wd.corp_id = #{corpId} LIMIT 1) as department FROM we_customer wc LEFT JOIN we_flower_customer_rel wfcr ON wc.external_userid=wfcr.external_userid AND wc.corp_id = wfcr.corp_id LEFT JOIN we_user wu ON wu.user_id=wfcr.user_id and wu.corp_id = wc.corp_id LEFT JOIN we_flower_customer_tag_rel wfcrf ON wfcrf.flower_customer_rel_id = wfcr.id LEFT JOIN we_tag wt ON wt.tag_id = wfcrf.tag_id LEFT JOIN we_customer_extend_property_rel wcepr ON wc.external_userid = wcepr.external_userid AND wcepr.corp_id = wc.corp_id AND wfcr.user_id = wcepr.user_id WHERE wc.external_userid=#{externalUserid} and wc.corp_id = #{corpId}
AND wfcr.user_id=#{userId}
ORDER BY wfcr.create_time ASC limit 1

SELECT wc.external_userid, wc.corp_id, wc.`name`, wc.avatar, wc.type, wc.gender, wc.unionid, wc.birthday, wc.corp_name, wc.corp_full_name, wc.position, wc.is_open_chat, wc.type, wfcr.id as relId, wfcr.user_id, wfcr.remark, wfcr.description, wfcr.create_time, wfcr.remark_corp_name, wfcr.oper_userid, wfcr.add_way, wfcr.state, wfcr.description, wfcr.remark_mobiles, wfcr.email, wfcr.address, wfcr.status, wfcr.wechat_channel, wu.user_name, wfcrf.flower_customer_rel_id, wt.tag_id, wt.create_time, wt.`name` as tagName, wcepr.corp_id as wceprCorpId, wcepr.external_userid as wceprExternalUserid, wcepr.extend_property_id, wcepr.property_value, wcepr.user_id as wceprUserId, (SELECT wd.`name` FROM we_department wd WHERE wd.id=wu.main_department and wd.corp_id = #{corpId} LIMIT 1) as department FROM we_customer wc LEFT JOIN we_flower_customer_rel wfcr ON wc.external_userid=wfcr.external_userid AND wc.corp_id = wfcr.corp_id LEFT JOIN we_user wu ON wu.user_id=wfcr.user_id and wu.corp_id = wc.corp_id LEFT JOIN we_flower_customer_tag_rel wfcrf ON wfcrf.flower_customer_rel_id = wfcr.id LEFT JOIN we_tag wt ON wt.tag_id = wfcrf.tag_id LEFT JOIN we_customer_extend_property_rel wcepr ON wc.external_userid = wcepr.external_userid AND wcepr.corp_id = wc.corp_id AND wfcr.user_id = wcepr.user_id WHERE wc.external_userid=#{externalUserid} and wc.corp_id = #{corpId}
AND wfcr.user_id=#{userId}
LIMIT 1

SELECT (
SELECT count(*)
FROM we_flower_customer_rel wfcr
WHERE wfcr.external_userid = wc.external_userid
AND wfcr.corp_id = #{corpId}
) AS addEmployeNum,
(
SELECT COUNT(*)
FROM we_group_member wgm
LEFT JOIN we_group wg on wg.chat_id = wgm.chat_id
where wgm.union_id = wc.external_userid
and wg.corp_id = #{corpId}
) AS addGroupNum,
(
SELECT COUNT(*)
FROM we_group_member
where user_id = #{userid}
and chat_id in (
SELECT wgm.chat_id
FROM `we_group_member` wgm
LEFT JOIN we_group wg on wg.chat_id = wgm.chat_id
where wg.corp_id = #{corpId}
and wgm.union_id = wc.external_userid
)
) AS commonGroupNum
FROM we_customer wc
WHERE corp_id = #{corpId}
and wc.external_userid = #{externalUserid}

mapper.sql:
SELECT COUNT(DISTINCT external_userid)
FROM we_flower_customer_rel
WHERE corp_id = #{corpId}
AND status IN (
${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code},
${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code},
${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}
)

