mapper.update:
UPDATE we_customer_messgaeResult


        send_time=#{sendTime},


        status=#{status},


        remark=#{remark}



    message_id=#{messageId}


            AND chat_id=#{chatId}


            AND userid = #{userId}



        AND external_userid=#{externalUserid}

mapper.select:
SELECT wcm.message_id, m.msgid FROM we_customer_messgaeResult wcm LEFT JOIN we_customer_message m ON m.message_id = wcm.message_id LEFT JOIN we_customer_messageoriginal wcmo ON wcmo.message_original_Id = m.original_id
wcmo.corp_id = #{corpId} AND wcm.`status`= 0 AND wcm.create_time BETWEEN #{startTime} AND #{endTime}
GROUP BY wcm.message_id
