mapper.delete:
delete
from we_emple_code
where corp_id = #{corpId}
  and id = #{id}

mapper.select:
select a.*,IFNULL(wd.name,'') mainDepartmentName
from (

and wecode.code_type != ${@com.easyink.common.constant.WeConstans@BATCH_SINGLE_EMPLE_CODE_TYPE}
and wecode.corp_id = #{corpId}

    and wecode.scenario like concat('%', #{scenario}, '%')


    AND wecode.create_by like concat('%', #{createBy}, '%')


    AND wecode.create_time >= #{beginTime}


    AND wecode.create_time <= #{endTime}


    AND wecusn.business_name like concat('%', #{useUserName}, '%')


    AND wecode.source = #{source}

group by wecode.id
order by wecode.create_time DESC
) a
LEFT JOIN we_user wu ON a.create_by = wu.user_name AND wu.corp_id = #{corpId}
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = #{corpId}

select wecode.id as emple_code_id, wecode.welcome_msg , wm.category_id, wm.material_url, wm.content, wm.material_name, wm.digest, wm.cover_url from we_emple_code wecode left join we_emple_code_use_scop wecus on wecode.id = wecus.emple_code_id left join we_material wm on wm .id = wecode.media_id where wecode.corp_id = #{corpId} and wecode.del_flag = ${@com.easyink.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}
and wecode.scenario = #{scenario}
and wecus.business_id = #{userId}
