SELECT wg.chat_id, wg.group_name, wg.create_time, wg.notice, wu.user_name as groupLeaderName, (SELECT COUNT(*) FROM we_group_member wgm WHERE wgm.chat_id=wg.chat_id) as memberNum, (SELECT wd.name FROM we_department wd WHERE wu.main_department = wd.id AND wd.corp_id = wg.corp_id) as mainDepartmentName, wg.status, wg.`owner` FROM we_group wg LEFT JOIN we_user wu ON wg.corp_id = wu.corp_id AND wg.`owner`=wu.user_id
wg.corp_id = #{corpId} ${params.dataScope} AND wg.chat_id = #{chatId} AND wg.group_name like concat('%', #{groupName}, '%') AND wu.user_name like concat('%', #{groupLeader}, '%')
AND wg.create_time >= #{beginTime}
AND wg.create_time <= #{endTime}
AND wg.`owner` in
#{item}
AND wg.`owner`=#{userIds}
AND wg.status in(${@com.easyink.common.constant.GroupConstants@NARMAL},${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE_EXTEND_SUCCESS})
order by wg.create_time desc

SELECT wg.`owner` FROM we_group wg LEFT JOIN we_user wu ON ( wg.`owner` = wu.user_id AND wu.corp_id = wg.corp_id ) LEFT JOIN we_department wd ON wd.id = wu.department WHERE wu.corp_id = #{corpId}
AND wu.main_department in
#{item}
