SELECT wg.chat_id, wg.group_name, wg.create_time, wg.notice, wu.user_name as groupLeaderName, (SELECT COUNT(*) FROM we_group_member wgm WHERE wgm.chat_id=wg.chat_id) as memberNum, (SELECT wd.name FROM we_department wd WHERE wu.main_department = wd.id AND wd.corp_id = wg.corp_id) as mainDepartmentName, wg.status, wg.`owner`, IF( wg.`status` = ${@com.easyink.common.constant.GroupConstants@NARMAL} OR wg.`status` = ${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE_EXTEND_SUCCESS}, 0, 1 ) groupStatus FROM we_group wg LEFT JOIN we_user wu ON wg.corp_id = wu.corp_id AND wg.`owner`= wu.user_id LEFT JOIN we_group_tag_rel wgtr ON wgtr.corp_id = wg.corp_id AND wgtr.chat_id = wg.chat_id LEFT JOIN we_group_tag wgt ON wgtr.corp_id = wgt.corp_id AND wgtr.tag_id = wgt.id WHERE wg.corp_id = #{corpId} ${params.dataScope}
AND wg.chat_id = #{chatId}
AND wg.group_name like concat('%', #{groupName}, '%')
AND wu.user_name like concat('%', #{groupLeader}, '%')
AND wg.create_time >= #{beginTime}
AND wg.create_time <= #{endTime}
AND wg.status IN (${@com.easyink.common.constant.GroupConstants@NARMAL},${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE_EXTEND_SUCCESS})
AND wg.status IN (${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE},${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE_EXTEND})
AND wgt.`id` in
#{item}
AND wgt.`id`=#{tagIds}
AND wu.user_id in
#{item}
AND wu.user_id = #{ownerIds}
GROUP BY wg.chat_id ORDER BY wg.create_time DESC

