mapper.select:
SELECT a.*,IFNULL(wd.name,'') mainDepartmentName
FROM (


        wptg.corp_id = #{corpId}
        AND  wptg.`del_flag` = ${@com.easyink.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}

            AND wptg.task_name LIKE CONCAT("%",#{taskName},"%")


            AND wptg.send_type = #{sendType}


            AND wptg.create_time >= #{beginTime}



            AND wptg.create_time <= #{endTime}


            AND wptg.create_by LIKE CONCAT("%",#{createBy},"%")


    order by wptg.`create_time` desc
) a
LEFT JOIN we_user wu ON a.create_by = wu.user_name AND wu.corp_id = #{corpId}
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = #{corpId}


LEFT JOIN we_pres_tag_group_scope wptgs ON wptgs.task_id = wptg.task_id
WHERE wptgs.we_user_id = #{emplId}
AND wptgs.is_done = #{isDone}
AND wptg.send_type = 1 AND wptg.`del_flag` = 0;

SELECT customer.`external_userid` FROM we_customer customer WHERE 1 = 1
AND customer.external_userid in ( SELECT t3.external_userid FROM we_pres_tag_group task LEFT JOIN we_pres_tag_group_tag t1 ON t1.task_id = task.task_id LEFT JOIN we_flower_customer_tag_rel t2 ON t2.tag_id = t1.tag_id LEFT JOIN we_flower_customer_rel t3 ON t2.flower_customer_rel_id = t3.id WHERE task.task_id = #{taskId} )
AND customer.external_userid in ( SELECT t3.external_userid FROM we_pres_tag_group task LEFT JOIN we_pres_tag_group_scope scope ON scope.task_id = task.task_id LEFT JOIN we_flower_customer_rel t3 ON t3.user_id = scope.we_user_id LEFT JOIN we_customer customer ON customer.external_userid = t3.external_userid WHERE task.task_id = #{taskId} )
AND customer.`gender` = #{gender}
and customer.`create_time` >= #{beginTime}
and customer.`create_time` <= #{endTime}

SELECT wfcr.`external_userid`, wfcr.`user_id`, wfcr.`remark` FROM we_customer customer LEFT JOIN we_flower_customer_rel wfcr ON customer.`external_userid` = wfcr.`external_userid`
wfcr.`status` = 0 and wfcr.`corp_id` = #{corpId}
AND wfcr.`user_id` in
#{item}
AND customer.external_userid in ( SELECT t3.external_userid FROM we_flower_customer_rel t3 LEFT JOIN we_flower_customer_tag_rel t2 ON t2.flower_customer_rel_id = t3.id WHERE t2.`tag_id` in
#{item}
)
AND customer.`gender` = #{gender}
AND customer.`gender` = 0
and wfcr.`create_time` >= #{beginTime}
and wfcr.`create_time` <= #{endTime}
group by wfcr.`external_userid`

mapper.update:
UPDATE we_pres_tag_group
task_name = #{taskName}, send_type = #{sendType}, group_code_id = #{groupCodeId}, welcome_msg = #{welcomeMsg}, send_scope = #{sendScope}, send_gender = #{sendGender}, cus_begin_time = #{cusBeginTime}, cus_end_time = #{cusEndTime}, update_by = #{updateBy}, update_time = #{updateTime},
WHERE task_id = #{taskId} and corp_id = #{corpId}
