mapper.select:
SELECT
    IFNULL((SELECT COUNT(wucms.external_userid)
            FROM we_user_customer_message_statistics wucms
    LEFT JOIN we_user wu ON wu.corp_id = wucms.corp_id AND wu.user_id = wucms.user_id
    WHERE wucms.corp_id = #{corpId}
    ${params.dataScope}
    AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
    AND wucms.user_send_message_cnt > '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'

    ), 0) AS chatTotal,
    IFNULL(SUM(ucms.user_send_message_cnt), 0) AS sendContactCnt,
    IFNULL((SUM(ucms.user_send_message_cnt) / COUNT(ucms.external_userid)), 0) AS averageChatTotal,
    IFNULL((SELECT
                IF(IFNULL(COUNT(wucms.external_userid), 0)=0,0, CAST(IFNULL(SUM(wucms.first_reply_time_interval_alter_receive), 0) *1./ (COUNT(wucms.external_userid) * 60) as decimal(8,2)))
            FROM we_user_customer_message_statistics wucms
            LEFT JOIN we_user wu ON wu.corp_id = wucms.corp_id AND wu.user_id = wucms.user_id
            WHERE wucms.corp_id = #{corpId}
            AND wucms.first_reply_time_interval_alter_receive > '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
            AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
            ${params.dataScope}
            AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}

            ), 0) AS averageFirstReplyDuration,
    IFNULL((SELECT COUNT(wucms.id)
            FROM we_user_customer_message_statistics wucms
            LEFT JOIN we_user wu ON wu.corp_id = wucms.corp_id AND wu.user_id = wucms.user_id
            WHERE wucms.corp_id = #{corpId}
            ${params.dataScope}
            AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
              AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'

            ),0) AS customerActiveStartContactCnt,
    IFNULL((SELECT COUNT(wucms.id)
            FROM we_user_customer_message_statistics wucms
            LEFT JOIN we_user wu ON wu.corp_id = wucms.corp_id AND wu.user_id = wucms.user_id
            WHERE wucms.corp_id = #{corpId}
              AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
              AND wucms.user_send_message_cnt != 0
            ${params.dataScope}
            AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}

            ), 0) AS userReplyContactCnt,
    IFNULL(SUM(ucms.three_rounds_dialogue_flag),0) AS effectiveCommunicationCustomerCnt
FROM we_user_customer_message_statistics ucms
LEFT JOIN we_user wu ON wu.corp_id = ucms.corp_id AND wu.user_id = ucms.user_id
WHERE
    ucms.corp_id = #{corpId}
    ${params.dataScope}
    AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}

        AND ucms.send_time >= #{beginTime}


        AND ucms.send_time <= #{endTime}


        AND ucms.user_id IN

            #{userId}

