mapper.select:
SELECT wfor.`id` recordId, wfor.`external_user_id` externalUserId, wc.`name` externalUserName, wc.`avatar` externalUserHeadImage, wfor.`employees` user_id, wu.`user_name`, wd.`name` departmentName, wfor.`channel_type`, wfor.`create_time`, wfor.`commit_time` FROM we_form_oper_record wfor LEFT JOIN we_customer wc ON wfor.external_user_id = wc.external_userid LEFT JOIN we_user wu ON wu.user_id = wfor.employees AND wu.corp_id = wc.corp_id LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id WHERE wfor.form_id = #{formId}
AND wfor.`create_time` >= #{beginTime}
AND wfor.`create_time` <= #{endTime}
AND wfor.`commit_time` >= #{beginTime}
AND wfor.`commit_time` <= #{endTime}
AND wc.`name` LIKE #{likeCustomerName}
AND wfor.`channel_type`= #{channelType}
AND wfor.commit_flag = TRUE AND wfor.`external_user_id` <> '' ORDER BY wfor.`commit_time` DESC

SELECT wfor.`user_id`,
       wfor.`user_name`,
       wfor.`user_head_image`,
       IFNULL(wd.`name`, '') departmentName,
(SELECT COUNT(DISTINCT external_user_id) FROM we_form_oper_record WHERE `form_id` = wfor.`form_id` AND `user_id` = wfor.`user_id` AND `external_user_id` <> '') AS clickCount,
(SELECT COUNT(DISTINCT external_user_id) FROM we_form_oper_record WHERE `form_id` = wfor.`form_id` AND `user_id` = wfor.`user_id` AND commit_flag = TRUE AND `external_user_id` <> '') AS submitCount
FROM we_form_oper_record wfor
         LEFT JOIN we_form wf ON wf.`id` = wfor.`form_id`
         LEFT JOIN we_user wu ON wu.user_id = wfor.user_id
         LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id
WHERE
    wfor.form_id = #{formId}
    AND wf.corp_id = #{corpId}
    AND wfor.`external_user_id` <> ''


            AND wfor.`create_time` >= #{beginTime}


            AND wfor.`create_time` <= #{endTime}




            AND wfor.`commit_time` >= #{beginTime}


            AND wfor.`commit_time` <= #{endTime}




        AND wfor.`user_name` LIKE #{likeUserName}

    GROUP BY wfor.`user_id`
    ORDER BY clickCount DESC

SELECT wfor.`external_user_id` externalUserId,
wc.`name` externalUserName,
wc.`avatar` externalUserHeadImage,
wfor.`form_result`,
wfor.`channel_type`,
wfor.`employees` userId,
wu.`user_name`,
wd.`name`,
wfor.`create_time`,
wfor.`commit_time`
FROM we_form_oper_record wfor
LEFT JOIN we_customer wc ON wfor.external_user_id = wc.external_userid
LEFT JOIN we_user wu ON wu.user_id = wfor.employees AND wu.corp_id = wc.corp_id
LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id
WHERE
wfor.form_id = #{formId}
AND wc.corp_id = #{corpId}
AND wfor.commit_flag = TRUE
AND wfor.`external_user_id` <> ''


        AND wfor.`create_time` >= #{beginTime}


        AND wfor.`create_time` <= #{endTime}




        AND wfor.`commit_time` >= #{beginTime}


        AND wfor.`commit_time` <= #{endTime}




    AND wc.`name` LIKE #{likeCustomerName}


    AND wfor.`channel_type`= #{channelType}

ORDER BY wfor.`commit_time` DESC
