mapper.select:
select sumClick.sumClickPersonNum,
       sumClick.sumClickNum,
       nowadaysClick.nowadaysClickPersonNum,
       nowadaysClick.nowadaysClickNum
from (
         SELECT COUNT(DISTINCT external_user_id) as sumClickPersonNum,
                COUNT(id)                        as sumClickNum,
                radar_id
         FROM we_radar_click_record
         WHERE radar_id = #{radarId}
     ) as sumClick
         left join
     (
         SELECT COUNT(DISTINCT external_user_id) as nowadaysClickPersonNum,
                COUNT(id)                        as nowadaysClickNum,
                radar_id
         FROM we_radar_click_record
         WHERE radar_id = #{radarId}
           and create_date = #{nowDate}
     ) as nowadaysClick on sumClick.radar_id = nowadaysClick.radar_id

SELECT COUNT(DISTINCT external_user_id) as `clickPersonNum`, COUNT(id) as `sumClickNum`, create_date as createDate FROM we_radar_click_record WHERE radar_id = #{radarAnalyse.radarId}
AND channel_name = #{radarAnalyse.channelName}
GROUP BY create_date

SELECT external_user_id externalId, external_user_name `customerName`, COUNT(id) clickNum, (select channel_name from we_radar_click_record where radar_id = #{customerRecord.radarId} and external_user_id = externalId order by create_time desc limit 1) as channelName, MAX(create_time) clickTime, external_user_head_image headImageUrl FROM we_radar_click_record WHERE radar_id = #{customerRecord.radarId}
AND external_user_name like concat('%', #{customerRecord.customerName}, '%')
AND create_date >= #{customerRecord.beginTime}
AND create_date <= #{customerRecord.endTime}
group by external_user_id
ORDER BY clickNum asc, clickTime desc
ORDER BY clickNum desc, clickTime desc
ORDER BY clickTime desc

SELECT channel_name `channelName`, COUNT(id) clickNum, COUNT(DISTINCT channel_name) clickPersonNum FROM we_radar_click_record WHERE radar_id = #{channelRecord.radarId}
AND channel_name like concat('%', #{channelRecord.channelName}, '%')
group by `channelName` order by clickNum desc

SELECT external_user_id externalId, external_user_name `customerName`, COUNT(external_user_id) clickNum, MAX(create_time) clickTime, channel_name channelName, external_user_head_image headImageUrl FROM we_radar_click_record WHERE radar_id = #{channelRecordDetail.radarId} AND channel_name = #{channelRecordDetail.channelName}
AND external_user_name like concat('%', #{channelRecordDetail.customerName}, '%')
GROUP BY external_user_id order by clickTime desc
